 insert into user_kposminin.full_app_from_short_visits  select        a.phone_num,        a.call_ymd,        v.url_fragment,        v.duration_sec,         v.visit_count,         v.average_visit_hour,         v.load_src,         v.ymd as visit_ymd   from user_kposminin.full_app_from_short_id a  inner join prod_odd.visit_feature v on v.id = a.id  where v.ymd = \'#ymd0\'    and a.call_ymd between date_add(\'#ymd0\',1) and date_add(\'#ymd0\', 180)        
